mapper.select:
SELECT

FROM t_user_coin_stream
WHERE biz_type = #{bizType, jdbcType=VARCHAR}
AND biz_id = #{bizId, jdbcType=VARCHAR}
AND deleted = 0
LIMIT 1

SELECT COUNT(1) > 0
FROM t_user_coin_stream
WHERE biz_type = #{bizType, jdbcType=VARCHAR}
AND biz_id = #{bizId, jdbcType=VARCHAR}
AND deleted = 0
